mapper.insert:

INSERT INTO NOTICE(
			NOTICE_NO,
			BOARD_CODE,
			NOTICE_TITLE,
			NOTICE_CONTENT,
			USER_ID,
			NOTICE_DATE,
			NOTICE_COUNT,
			ORIGIN_NAME,
			CHANGE_NAME)

			VALUES(SEQ_NOTICENO.NEXTVAL
					,#{boardCode}
					,#{noticeTitle}
					,#{noticeContent}
					,#{userId}
					,SYSDATE
					,#{noticeCount}
					,#{originName}
					,#{changeName})

mapper.update:
UPDATE NOTICE
SET NOTICE_TITLE = #{noticeTitle}
   ,BOARD_CODE = #{boardCode}
   ,NOTICE_CONTENT = #{noticeContent}
   ,ORIGIN_NAME = #{originName}
   ,CHANGE_NAME = #{changeName}
WHERE NOTICE_NO = #{noticeNo}
AND STATUS = 'Y'
