mapper.insert:
INSERT INTO PAYMENT (PAYMENT_NO
				   , USER_ID
				   , PAYMENT_METHOD
				   , PAYMENT_DATE
				   , PAYMENT_DETAIL_TIME
				   , PAYMENT_STATUS
				   , PAYMENT_AMOUNT
				   , DEPOSIT_NAME
				   , DEPOSIT_BANK
				   , CARDCOMPANY_CODE
				   , CARD_NO)
VALUES(SEQ_PAYNO.NEXTVAL
	 , #{userId}
	 , #{paymentMethod}
	 , DEFAULT
	 , #{paymentDetailTime}
	 , #{paymentStatus}
	 , #{paymentAmount}
	 , #{depositName}
	 , #{depositBank}
	 , #{cardCompanyCode}
	 , #{cardNo})

mapper.select:
SELECT PAYMENT_NO
FROM PAYMENT
WHERE USER_ID = #{userId}
AND PAYMENT_DETAIL_TIME = #{paymentDetailTime}

mapper.insert:
INSERT INTO PURCHASE (PURCHASE_NO
				   , USER_ID
				   , PERFO_TITLE
				   , HALL_NAME
				   , SEAT_LIST
				   , PAYMENT_NO
				   , PERFO_NO
				   , CUSTOMERSERVICE_STATUS
				   , PURCHASE_DATE
				   , RESERVATION_DATE
				   , PERFO_START_TIME
				   , SEAT_PRICE)
VALUES(SEQ_PURCHASENO.NEXTVAL
	 , #{userId}
	 , #{perfoTitle}
	 , #{hallName}
	 , #{seatList}
	 , #{paymentNo}
	 , #{perfoNo}
	 , #{customerserviceStatus}
	 , DEFAULT
	 , #{reservationDate}
	 , #{perfoStartTime}
	 , #{seatPrice})

mapper.delete:
DELETE 
FROM PURCHASE
WHERE USER_ID = #{userId}
AND HALL_NAME IS NULL
AND PERFO_NO = #{perfoNo}

mapper.select:

        SELECT PAYMENT_NO, PAYMENT_DATE, PAYMENT_METHOD, PAYMENT_AMOUNT, PERFO_NO, PERFO_TITLE, HALL_NAME, PAYMENT_STATUS
		FROM PAYMENT
		JOIN PURCHASE USING(PAYMENT_NO)
        WHERE PAYMENT.USER_ID = #{userId} 
        AND PURCHASE_DATE >= #{startDate} 
		AND PURCHASE_DATE <= #{endDate} 
        GROUP BY PAYMENT_NO, PAYMENT_DATE, PAYMENT_METHOD, PAYMENT_AMOUNT, PERFO_NO, PERFO_TITLE, HALL_NAME, PAYMENT_STATUS
        ORDER BY PAYMENT_NO DESC

mapper.update:
UPDATE PAYMENT
SET PAYMENT_AMOUNT = #{paymentAmount}, 
	PAYMENT_STATUS = #{paymentStatus}
WHERE PAYMENT_NO = #{paymentNo}
